import React, { useState } from "react";
import 'bulma/css/bulma.css'
// Styles
import "./index.css";

// Components
import CodeEditor from "./Code";

export default function App() {
  const [editorLanguage, setEditorLanguage] = useState("javascript");

  return (
    <div className="App container">
      <div class="is-size-2">Dyte code syntax hightlighter</div>

      <fieldset>
        <legend>Choose language:</legend>
        <input
          type="radio"
          id="html"
          name="language"
          value="markup"
          checked={editorLanguage === "markup"}
          onChange={() => setEditorLanguage("markup")}
        />
        <label htmlFor="xml">HTML</label>
        <input
          type="radio"
          id="css"
          name="language"
          value="css"
          checked={editorLanguage === "css"}
          onChange={() => setEditorLanguage("css")}
        />
        <label htmlFor="css">CSS</label>
        <input
          type="radio"
          id="javascript"
          name="language"
          value="javascript"
          checked={editorLanguage === "javascript"}
          onChange={() => setEditorLanguage("javascript")}
        />
        <label htmlFor="javascript">JavaScript</label>
      </fieldset>

      <CodeEditor language={editorLanguage} />
    </div>
  );
}
